evs-editor-Example 2-Alice:
filter = (event) => {return event.user === 'Alice'};

initialState = {balance:0,history:[]};

onCommand = (command, state, persist) => {
    log("onCommand")
    return {
      'PAY_IN': ()=> {
        const event = copy(command);
        event.type = 'PAYED_IN';
        persist(event);
        sendMail("You payed in "+event.amount);
      },
      'PAY_OUT' : ()=> {
        const event = copy(command);
        event.type = 'PAYED_OUT';
        persist(event);
      }
    }[command.type]();
  };

onEvent = (event, state) => {
 return {
  'PAYED_IN': () => {
   state.balance = state.balance + event.amount;
   state.history = state.history
    .concat([state.balance])
    .slice(-5);
   return state;
  },
  'PAYED_OUT': () => {

   state.balance = state.balance - event.amount
   state.history = state.history
    .concat([state.balance])
    .slice(-5);
   return state;
  }
 }[event.type]();
};

evs-editor-Example 2-Bob:
filter = (event) => event.user === 'Bob'

initialState = {
 balance: 0,
 history: []
};

onEvent = (event, state) => {
 return {
  'PAYED_IN': () => {
   state.balance = state.balance + event.amount * 2;
   state.history = state.history
    .concat([state.balance])
    .slice(-5);
   return state;
  },
  'PAYED_OUT': () => {
   state.balance = (state.balance || 0) - event.amount
   state.history = (state.history || [])
    .concat([state.balance])
    .slice(-5);
   return state;
  }
 }[event.type]();
};

